package 第34场双周赛;

/**
 * @ClassName NumberOfWaysToSplitaString
 * @Description #1573 分割字符串的方案数
 * @Author MatthewHan
 * @Date 2020/9/5 19:52
 * @Version 1.0
 **/
public class No1573 {


    public static int numWays(String s) {
        int count1 = 0;
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == '1') {
                count1++;
            }
        }
        if (count1 == 0) {
            int a = s.length() - 2;
            long sum = 0;
            for (int i = 1; i <= a; i++) {
                sum += i;
            }
            return (int) (sum % 1000000007);

        }
        if (count1 % 3 != 0) {
            return 0;
        }
        int var = count1 / 3;
        long sub1 = 1;
        long sub2 = 1;
        int tmp = 0;
        boolean flag1 = false;
        boolean flag2 = false;
        for (int k = 0; k < s.length(); k++) {
            if (s.charAt(k) == '1') {
                tmp++;
            }
            if (tmp == var) {
                flag1 = true;
            }
            if (tmp == var * 2) {
                flag2 = true;
            }
            if (flag1 && tmp == var && s.charAt(k) == '0') {
                sub1 %= 1000000007;
                sub1++;
            }
            if (flag2 && tmp == var * 2 && s.charAt(k) == '0') {
                sub2 %= 1000000007;
                sub2++;
            }
        }
        System.out.println("sub1 = " + sub1);
        System.out.println("sub2 = " + sub2);
        return (int) (sub1 * sub2 % 1000000007);

    }


    public static void main(String[] args) {
        System.out.println(numWays(""));

    }
}
